import React, { useState } from "react";
import './calculator.css';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHistory } from '@fortawesome/free-solid-svg-icons';
import Slider from 'react-input-slider';

const btnValues = [
  ["C", "^", "%", "/"],
  [7, 8, 9, "*"],
  [4, 5, 6, "-"],
  [1, 2, 3, "+"],
  [0, ".", "="],
];

const Calculator = () => {
  const [value, setValue] = useState('');
  const [isShadowActive, setIsShadowActive] = useState(false);
  const [shadowValue, setShadowValue] = useState('');
  const [angleUnit, setAngleUnit] = useState('rad');
  const [openBracket, setOpenBracket] = useState(0);

  const [historyVisible, setHistoryVisible] = useState(false);
  const [sliderValue, setSliderValue] = useState(0);


  const scientificBtnValues = [
    [
      <div key="angle-unit" className='angle-unit'>
        <label className="small-buttons">
          <input
            type="radio"
            value="deg"
            checked={angleUnit === 'deg'}
            onChange={() => handleAngleUnitChange('deg')}
          />
          &nbsp;Deg&nbsp;&nbsp;&nbsp;
        </label>
        <label className="small-buttons">
          <input
            type="radio"
            value="rad"
            checked={angleUnit === 'rad'}
            onChange={() => handleAngleUnitChange('rad')}
          />
          &nbsp;Rad
        </label>
      </div>,
      <button
        key="history"
        onClick={() => setHistoryVisible(!historyVisible)}
        className="small-buttons"
        style={{ marginTop: '-10px', background: 'none', border: 'none' }}
      >
        <FontAwesomeIcon icon={faHistory} />
      </button>
    ],
    ["√", "π", "(", ")"],
    ["log", "ln", "e"],
    ["sin", "cos", "tan"],
    ["sinh", "cosh", "tanh"], // Hyperbolic functions
  ];

  const handleAngleUnitChange = (unit) => {
    setAngleUnit(unit);
  };

  const handleSliderChange = (value) => {
    setSliderValue(value.x);
  };

  const handleButtonClick = async (buttonValue) => {
    if (buttonValue === 'C') {
      setValue('');
      setIsShadowActive(false);
      setShadowValue('');
      setOpenBracket(1);
    } else if (buttonValue === '=') {
      try {
        let expression = value;
        if (isShadowActive) {
          expression += shadowValue;
          setIsShadowActive(false);
          setShadowValue('');
        }

        const response = await axios.post('http://localhost:8000/api/calculate/', {
          expression: expression,
          angleUnit: angleUnit,
        });
        const result = response.data.result;
        setValue(result);
      } catch (error) {
        console.error('Error sending data to the backend:', error);
      }
    } else {
      setValue((prevValue) => {
        if (buttonValue === '(') {
          setIsShadowActive(true);
          setOpenBracket(openBracket + 1);
          setShadowValue(')'.repeat(openBracket));
        }
        return prevValue + buttonValue;
      });
    }
  };

  const viewHistory = () => {
    console.log('Viewing history...');
    // Implement your logic to show the history container
  };

  const renderBoxes = () => {
    const boxes = [];
    const boxWidth = 25;
    const boxHeight = 15;
    const totalBoxes = 5;

    for (let i = 0; i < totalBoxes; i++) {
      boxes.push(
        <div
          key={i}
          style={{
            width: boxWidth,
            height: boxHeight,
            backgroundColor: '#007bff',
            marginRight: i < totalBoxes - 1 ? '5px' : '0',
          }}
        />
      );
    }

    return boxes;
  };

  return (
    <div className="calculator">
      {historyVisible && (
        <div className="history-container">
          <div style={{ display: 'flex', marginTop: '10px' }}>{renderBoxes()}</div>

          {/* Add your history content here */}
          <Slider
            axis="x"
            x={sliderValue}
            xmin={0}
            xmax={500} // Adjust this based on your display text field length
            onChange={handleSliderChange}
            styles={{
              track: {
                backgroundColor: '#e0e0e0',
                height: 15,
                width: '100%', // Adjusted to the full width
              },
              active: {
                backgroundColor: '#007bff',
                height: 15,
              },
              thumb: {
                width: 25,
                height: 15,
                marginTop: -7.5,
                backgroundColor: '#007bff',
                borderRadius: 0,
              },
            }}
          />
        </div>
      )}

      <div className='display'>
        <input
          type="text"
          readOnly
          className={`display-text ${isShadowActive ? 'shadow-active' : ''}`}
          value={isShadowActive ? `${value}${shadowValue}` : value}
        />
      </div>
      <div className="btn-container">
        <div className="scientific">
          {scientificBtnValues.map((row, rowIndex) => (
            <div key={rowIndex} className="row">
              {row.map((buttonValue, colIndex) => (
                <button
                  key={colIndex}
                  onClick={() => {
                    if (typeof buttonValue !== 'object') {
                      handleButtonClick(buttonValue);
                    }
                  }}
                  className={buttonValue === ')' && isShadowActive ? 'btn shadow-btn' : ''}
                >
                  {buttonValue}
                </button>
              ))}
            </div>
          ))}
        </div>

        <div className="basic">
          {btnValues.map((row, rowIndex) => (
            <div key={rowIndex} className="row">
              {row.map((buttonValue, colIndex) => (
                <button
                  key={colIndex}
                  onClick={() => handleButtonClick(buttonValue)}
                  className={buttonValue === '=' ? 'btn btn-primary ml-2' : ''}
                >
                  {buttonValue}
                </button>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Calculator;
