import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMicrophone } from "@fortawesome/free-solid-svg-icons";
import "./App.css";
import Axios from "axios"; // Import Axios

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchQuery: "",
      isMicrophoneClicked: false,
    };
    this.mediaRecorder = null;
    this.audioChunks = [];
  }

  handleSearchChange = (event) => {
    // Trim the input value to remove leading and trailing spaces
    const searchQuery = event.target.value.trim();
    this.setState({ searchQuery });
  };

  handleSearch = () => {
    const { searchQuery } = this.state;
    Axios.post("http://localhost:8000/api/search/", { search_query: searchQuery }) // Make sure to use 'search_query' as the key
      .then((response) => {
        console.log("Search result:", response.data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  handleMicrophoneClick = () => {
    // Toggle the isMicrophoneClicked state
    this.setState((prevState) => ({
      isMicrophoneClicked: !prevState.isMicrophoneClicked,
    }));

    if (!this.state.isMicrophoneClicked) {
      // Start recording
      this.startRecording();
    } else {
      // Stop recording
      this.stopRecording();
    }
  };

  startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      this.mediaRecorder = new MediaRecorder(stream);
      this.audioChunks = [];

      this.mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          this.audioChunks.push(event.data);
        }
      };

      this.mediaRecorder.onstop = () => {
        const audioBlob = new Blob(this.audioChunks, { type: 'audio/wav' });

        // Send audioBlob to backend using Axios
        this.sendAudioToBackend(audioBlob);

        this.audioChunks = [];
        this.mediaRecorder = null;
      };

      this.mediaRecorder.start();
    } catch (error) {
      console.error('Error starting recording:', error);
    }
  };

  stopRecording = () => {
    if (this.mediaRecorder) {
      this.mediaRecorder.stop();
    }
  };

  sendAudioToBackend = async (audioBlob) => {
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, "audio.webm");

      const response = await Axios.post("http://localhost:8000/api/audio_transcription/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.status === 200) {
        const transcribedText = response.data.transcription;
        this.setState({ searchQuery: transcribedText });
      } else {
        console.error('Server returned an unexpected status:', response.status);
      }
    } catch (error) {
      console.error('Error sending audio to backend:', error);
    }
  };



  render() {
    const { isMicrophoneClicked } = this.state;
    const microphoneButtonClass = `btn-microphone ${isMicrophoneClicked ? 'pulsing' : ''}`;
    const placeholderText = isMicrophoneClicked ? "Listening..." : "Enter your search query";

    return (
      <div className="container d-flex align-items-center justify-content-center vh-100">
        <div className="text-center">
          <h1 className="mb-4">Orthoquant</h1>
          <div className="input-group mb-3">
            <div style={{ position: "relative" }}>
              <input
                type="text"
                className="form-control"
                placeholder={placeholderText}
                value={this.state.searchQuery}
                onChange={this.handleSearchChange}
                style={{ width: '500px', paddingRight: '40px'  }} // Set the width to 800 pixels
              />
              <button
                className={microphoneButtonClass}
                type="button"
                onClick={this.handleMicrophoneClick}
                style={{
                  position: "absolute",
                  top: 1,
                  right: 5,
                  bottom: 0,
                }}
              >
                <FontAwesomeIcon icon={faMicrophone} style={{ color: "black" }} />
              </button>
            </div>
            <div className="input-group-append">
              <button
                className="btn btn-primary ml-2"
                type="button"
                onClick={this.handleSearch}
              >
                Search
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
