import React, { useState } from "react";
import './calculator.css';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHistory } from '@fortawesome/free-solid-svg-icons';

const btnValues = [
  ["C", "^", "%", "/"],
  [7, 8, 9, "*"],
  [4, 5, 6, "-"],
  [1, 2, 3, "+"],
  [0, ".", "="],
];

const Calculator = () => {
  const [value, setValue] = useState('');
  const [isShadowActive, setIsShadowActive] = useState(false);
  const [shadowValue, setShadowValue] = useState('');
  const [angleUnit, setAngleUnit] = useState('rad'); // Default to "Rad"

  const [openBracket, setOpenBracket] = useState(0); // Default to "Rad"


  const scientificBtnValues = [
    [
      <div key="angle-unit" className='angle-unit'>
        <label className="small-buttons">
          <input
            type="radio"
            value="deg"
            checked={angleUnit === 'deg'}
            onChange={() => handleAngleUnitChange('deg')}
          />
          &nbsp;Deg&nbsp;&nbsp;&nbsp;
        </label>
        <label className="small-buttons">
          <input
            type="radio"
            value="rad"
            checked={angleUnit === 'rad'}
            onChange={() => handleAngleUnitChange('rad')}
          />
          &nbsp;Rad
        </label>
      </div>,
      <button
          key="history"
          onClick={() => viewHistory()}
          className="small-buttons"
          style={{ marginTop: '-10px', background: 'none', border: 'none' }}
        >
          <FontAwesomeIcon icon={faHistory} />
        </button>

    ],
    ["√", "π", "(", ")"],
    ["log", "ln", "e"],
    ["sin", "cos", "tan"],
    ["sinh", "cosh", "tanh"], // Hyperbolic functions
  ];

  const handleAngleUnitChange = (unit) => {
    setAngleUnit(unit);
  };
  const handleButtonClick = async (buttonValue) => {
    if (buttonValue === 'C') {
      setValue('');
      setIsShadowActive(false);
      setShadowValue('');
      setOpenBracket(0); // Reset open parentheses count
    } else if (buttonValue === '=') {
      try {
        let expression = value;
        if (isShadowActive) {
          expression += shadowValue;
          setIsShadowActive(false);
          setShadowValue('');
        }

        // Check if the parentheses are balanced before sending the request
        if (expression.split('(').length === expression.split(')').length) {
          const response = await axios.post('http://localhost:8000/api/calculate/', {
            expression: expression,
            angleUnit: angleUnit,
          });

          const result = response.data.result;
          setValue(result);
        } else {
          console.error('Mismatched parentheses in the expression.');
        }
      } catch (error) {
        console.error('Error sending data to the backend:', error);
      }
    } else {
      setValue((prevValue) => {
        if (buttonValue === '(' && !isShadowActive) {
          setIsShadowActive(true);
          setOpenBracket(openBracket + 1);
          setShadowValue(')'.repeat(openBracket + 1));
        }
        if (buttonValue === ')' && openBracket > 0) {
          setOpenBracket(openBracket - 1);
          setShadowValue(')'.repeat(openBracket - 1));
        }
        return prevValue + buttonValue;
      });

    }
  };  


  const viewHistory = () => {
    // Implement your logic to view history here
    console.log('Viewing history...');
  };

  return (
    <div className="calculator">
      <div className='display'>
      <input
        type="text"
        readOnly
        className={`display-text ${isShadowActive ? 'shadow-active' : ''}`}
        value={isShadowActive ? `${value}${shadowValue}` : value}
      />
      </div>
      <div className="btn-container">
        <div className="scientific">
          {scientificBtnValues.map((row, rowIndex) => (
            <div key={rowIndex} className="row">
              {row.map((buttonValue, colIndex) => (
                <button
                  key={colIndex}
                  onClick={() => {
                    if (typeof buttonValue !== 'object') {
                      handleButtonClick(buttonValue);
                    }
                  }}
                >
                  {buttonValue}
                </button>
              ))}
            </div>
          ))}
        </div>

        <div className="basic">
          {btnValues.map((row, rowIndex) => (
            <div key={rowIndex} className="row">
              {row.map((buttonValue, colIndex) => (
                <button
                  key={colIndex}
                  onClick={() => handleButtonClick(buttonValue)}
                  className={buttonValue === '=' ? 'btn btn-primary ml-2' : ''}
                >
                  {buttonValue}
                </button>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Calculator;
